mapper.select:
select
AUTH_GUID as "authGuid"
,   WORKPLAN_GUID as "workplanGuid"
,   REFER_TASK_GUID as "referTaskGuid"
,   ROW_ORDER as "rowOrder"
,   EXTRA1 as "extra1"
,   EXTRA2 as "extra2"
,   EXTRA3 as "extra3"
,   EXTRA4 as "extra4"
,   EXTRA5 as "extra5"
,   EXTRA6 as "extra6"
,   EXTRA7 as "extra7"
,   EXTRA8 as "extra8"
,   EXTRA9 as "extra9"
,   EXTRA10 as "extra10"
,   ORG_GUID as "orgGuid"
,   ORG_CODE as "orgCode"
,   ORG_NAME as "orgName"
,   CREATE_DATE as "createDate"
,   UPDATE_DATE as "updateDate"
,   DELETE_DATE as "deleteDate"
,   CREATE_USER_LABEL as "createUserLabel"
,   UPDATE_USER_LABEL as "updateUserLabel"
,   DELETE_USER_LABEL as "deleteUserLabel"
,   CREATE_USER_NAME as "createUserName"
,   UPDATE_USER_NAME as "updateUserName"
,   DELETE_USER_NAME as "deleteUserName"
,   RECORD_VERSION as "recordVersion"
from ${projectSchema}T_ABTR_AUTH

    and  AUTH_GUID=#{authGuid}
    and  WORKPLAN_GUID=#{workplanGuid}
    and  REFER_TASK_GUID=#{referTaskGuid}
    and  ROW_ORDER=#{rowOrder}
    and  EXTRA1=#{extra1}
    and  EXTRA2=#{extra2}
    and  EXTRA3=#{extra3}
    and  EXTRA4=#{extra4}
    and  EXTRA5=#{extra5}
    and  EXTRA6=#{extra6}
    and  EXTRA7=#{extra7}
    and  EXTRA8=#{extra8}
    and  EXTRA9=#{extra9}
    and  EXTRA10=#{extra10}
    and  ORG_GUID=#{orgGuid}
    and  ORG_CODE=#{orgCode}
    and  ORG_NAME=#{orgName}
    and  CREATE_DATE=#{createDate}
    and  UPDATE_DATE=#{updateDate}
    and  DELETE_DATE=#{deleteDate}
    and  CREATE_USER_LABEL=#{createUserLabel}
    and  UPDATE_USER_LABEL=#{updateUserLabel}
    and  DELETE_USER_LABEL=#{deleteUserLabel}
    and  CREATE_USER_NAME=#{createUserName}
    and  UPDATE_USER_NAME=#{updateUserName}
    and  DELETE_USER_NAME=#{deleteUserName}
    and  RECORD_VERSION=#{recordVersion}

select
AUTH_GUID as "authGuid"
,   WORKPLAN_GUID as "workplanGuid"
,   REFER_TASK_GUID as "referTaskGuid"
,   ROW_ORDER as "rowOrder"
,   EXTRA1 as "extra1"
,   EXTRA2 as "extra2"
,   EXTRA3 as "extra3"
,   EXTRA4 as "extra4"
,   EXTRA5 as "extra5"
,   EXTRA6 as "extra6"
,   EXTRA7 as "extra7"
,   EXTRA8 as "extra8"
,   EXTRA9 as "extra9"
,   EXTRA10 as "extra10"
,   ORG_GUID as "orgGuid"
,   ORG_CODE as "orgCode"
,   ORG_NAME as "orgName"
,   CREATE_DATE as "createDate"
,   UPDATE_DATE as "updateDate"
,   DELETE_DATE as "deleteDate"
,   CREATE_USER_LABEL as "createUserLabel"
,   UPDATE_USER_LABEL as "updateUserLabel"
,   DELETE_USER_LABEL as "deleteUserLabel"
,   CREATE_USER_NAME as "createUserName"
,   UPDATE_USER_NAME as "updateUserName"
,   DELETE_USER_NAME as "deleteUserName"
,   RECORD_VERSION as "recordVersion"
from ${projectSchema}T_ABTR_AUTH

    and  AUTH_GUID=#{param.authGuid}
    and  WORKPLAN_GUID=#{param.workplanGuid}
    and  REFER_TASK_GUID=#{param.referTaskGuid}
    and  ROW_ORDER=#{param.rowOrder}
    and  EXTRA1=#{param.extra1}
    and  EXTRA2=#{param.extra2}
    and  EXTRA3=#{param.extra3}
    and  EXTRA4=#{param.extra4}
    and  EXTRA5=#{param.extra5}
    and  EXTRA6=#{param.extra6}
    and  EXTRA7=#{param.extra7}
    and  EXTRA8=#{param.extra8}
    and  EXTRA9=#{param.extra9}
    and  EXTRA10=#{param.extra10}
    and  ORG_GUID=#{param.orgGuid}
    and  ORG_CODE=#{param.orgCode}
    and  ORG_NAME=#{param.orgName}
    and param.CREATE_DATE=#{param.createDate}
    and param.UPDATE_DATE=#{param.updateDate}
    and param.DELETE_DATE=#{param.deleteDate}
    and  CREATE_USER_LABEL=#{param.createUserLabel}
    and  UPDATE_USER_LABEL=#{param.updateUserLabel}
    and  DELETE_USER_LABEL=#{param.deleteUserLabel}
    and  CREATE_USER_NAME=#{param.createUserName}
    and  UPDATE_USER_NAME=#{param.updateUserName}
    and  DELETE_USER_NAME=#{param.deleteUserName}
    and param.RECORD_VERSION=#{param.recordVersion}


    
        
            order by ${sortField} ${sortOrder}
        
        
            order by ${sortField} asc

select
count(1) from ${projectSchema}T_ABTR_AUTH

    and  AUTH_GUID=#{authGuid}
