mapper.update:
update ${projectSchema}T_ABTR_AUTH
WORKPLAN_GUID = #{workplanGuid}, REFER_TASK_GUID = #{referTaskGuid}, ROW_ORDER = #{rowOrder}, EXTRA1 = #{extra1}, EXTRA2 = #{extra2}, EXTRA3 = #{extra3}, EXTRA4 = #{extra4}, EXTRA5 = #{extra5}, EXTRA6 = #{extra6}, EXTRA7 = #{extra7}, EXTRA8 = #{extra8}, EXTRA9 = #{extra9}, EXTRA10 = #{extra10}, ORG_GUID = #{orgGuid}, ORG_CODE = #{orgCode}, ORG_NAME = #{orgName}, CREATE_DATE = #{createDate}, UPDATE_DATE = #{updateDate}, DELETE_DATE = #{deleteDate}, CREATE_USER_LABEL = #{createUserLabel}, UPDATE_USER_LABEL = #{updateUserLabel}, DELETE_USER_LABEL = #{deleteUserLabel}, CREATE_USER_NAME = #{createUserName}, UPDATE_USER_NAME = #{updateUserName}, DELETE_USER_NAME = #{deleteUserName}, RECORD_VERSION = #{recordVersion},
where AUTH_GUID=#{authGuid}

